changeSet.sql:

    create table awisc_data_format (
       id bigint not null,
        name varchar(255),
        primary key (id)
    ) engine=InnoDB;

    create table awisc_dataset (
       id bigint not null,
        description varchar(255),
        elevation varchar(255),
        last_update datetime,
        latitude varchar(255),
        longitude varchar(255),
        name varchar(255),
        url varchar(255),
        data_format_id bigint,
        ldsh_id bigint,
        update_frequency_id bigint,
        primary key (id)
    ) engine=InnoDB;

    create table awisc_ldsh (
       id bigint not null,
        name varchar(255),
        primary key (id)
    ) engine=InnoDB;

    create table awisc_setting (
       setting_key varchar(255) not null,
        setting_val varchar(255),
        primary key (setting_key)
    ) engine=InnoDB;

    create table awisc_update_frequency (
       id bigint not null,
        name varchar(255),
        primary key (id)
    ) engine=InnoDB;

    create table awisc_user (
       id bigint not null,
        password varchar(255),
        username varchar(255),
        primary key (id)
    ) engine=InnoDB;

    create table awisc_wmo_code (
       id bigint not null,
        name varchar(255),
        primary key (id)
    ) engine=InnoDB;

    create table dataset_codes (
       dataset_id bigint not null,
        wmo_code_id bigint not null
    ) engine=InnoDB;

    alter table awisc_dataset 
       add constraint FK_awisc_dataset_data_format 
       foreign key (data_format_id) 
       references awisc_data_format (id);

    alter table awisc_dataset 
       add constraint FK_awisc_dataset_ldsh 
       foreign key (ldsh_id) 
       references awisc_ldsh (id);

    alter table awisc_dataset 
       add constraint FK_awisc_dataset_update_frequency 
       foreign key (update_frequency_id) 
       references awisc_update_frequency (id);

    alter table dataset_codes 
       add constraint FK_awisc_wmo_code_dataset_codes 
       foreign key (wmo_code_id) 
       references awisc_wmo_code (id);

    alter table dataset_codes 
       add constraint FK_awisc_dataset_dataset_codes 
       foreign key (dataset_id) 
       references awisc_dataset (id);


